-------------------------------------------	ID: 5428--	Item: Scroll of Instant Retrace--  	Description: "Retrace" (Nation)-----------------------------------------require("scripts/globals/teleports");require("scripts/globals/settings");require("scripts/globals/status");------------------------------------------- OnItemCheck-----------------------------------------function onItemCheck(target)	result = 0;end;------------------------------------------- OnItemUse-----------------------------------------function onItemUse(target)	nation = target:getNation(); -- 0 = San d'Oria; 1 = Bastok; 2 = Windurst;	if (nation == 0) then		target:setPos(-98, 1, -41, 224, 0x50);	elseif (nation == 1) then		target:setPos(-291, -10, -107, 212, 0x57);	elseif (nation == 2) then		target:setPos(-31.442, -5, 129.202, 128, 0x5E);	endend;