-------------------------------------------	ID: 5990--	Scroll of Instant Stoneskin--  Grants the user the effect of Shell magic------------------------------------------------------------------------------------ OnItemCheck-----------------------------------------function onItemCheck(target)	return 0;end;------------------------------------------- OnItemUse-----------------------------------------function onItemUse(target)	local pAbs = 350;	local pEquipMods = 0;	local duration = 300;	-- equipment mods	if (target:getEquipID(SLOT_NECK) == 13177) then -- Stone Gorget		pEquipMods = pEquipMods + 30;	end	if (target:getEquipID(SLOT_HANDS) == 15034) then -- Stone Mufflers		pEquipMods = pEquipMods + 30;	end	if (target:getEquipID(SLOT_WAIST) == 15960) then -- Siegel Sash		pEquipMods = pEquipMods + 20;	end	if (target:getEquipID(SLOT_LEGS) == 11949) then -- Haven Hose		pEquipMods = pEquipMods + 20;	end	if (target:getEquipID(SLOT_LEGS) == 10329) then -- Shedir Seraweels		pEquipMods = pEquipMods + 35;	end	if (target:getEquipID(SLOT_LEGS) == 11037) then -- Earthcry Earring		pEquipMods = pEquipMods + 10;	end	local final = pAbs + pEquipMods;	target:addStatusEffect(EFFECT_STONESKIN,final,0,duration);	return EFFECT_STONESKIN;end;